mapper.select:
SELECT R.ID, R.FROM_ACCOUNT, R.TO_ACCOUNT, A_FROM.name AS FROM_ACCOUNT_NAME, A_TO.name AS TO_ACCOUNT_NAME, R.DESCRIPTION, R.PERCENTAGE, R.AMOUNT, R.RATIO, R.USER_ID FROM REGULAR_TRANSFER AS R LEFT OUTER JOIN ACCOUNT AS A_FROM ON A_FROM.ID = R.FROM_ACCOUNT LEFT OUTER JOIN ACCOUNT AS A_TO ON A_TO.ID = R.TO_ACCOUNT
R.USER_ID = #{userId}
ORDER BY ID

SELECT R.ID, R.FROM_ACCOUNT, R.TO_ACCOUNT, A_FROM.name AS FROM_ACCOUNT_NAME, A_TO.name AS TO_ACCOUNT_NAME, R.DESCRIPTION, R.PERCENTAGE, R.AMOUNT, R.RATIO, R.USER_ID FROM REGULAR_TRANSFER AS R LEFT OUTER JOIN ACCOUNT AS A_FROM ON A_FROM.ID = R.FROM_ACCOUNT LEFT OUTER JOIN ACCOUNT AS A_TO ON A_TO.ID = R.TO_ACCOUNT
R.ID = #{id}
AND R.USER_ID = #{userId}

mapper.update:
UPDATE REGULAR_TRANSFER
SET
FROM_ACCOUNT = #{fromAccount},
TO_ACCOUNT = #{toAccount},
DESCRIPTION = #{description},
PERCENTAGE = #{percentage},

    RATIO = #{ratio}


    AMOUNT = #{amount}

WHERE
ID = #{id}
AND USER_ID = #{userId}

mapper.delete:
DELETE FROM REGULAR_TRANSFER

    ID = #{id}
    AND USER_ID = #{userId}
