mapper.select:
SELECT T.ID, T.FROM_ACCOUNT, T.TO_ACCOUNT, A_FROM.name AS FROM_ACCOUNT_NAME, A_TO.name AS TO_ACCOUNT_NAME, T.DESCRIPTION, T.USER_ID FROM TEMPLATE_TRANSFER AS T LEFT OUTER JOIN ACCOUNT AS A_FROM ON A_FROM.ID = T.FROM_ACCOUNT LEFT OUTER JOIN ACCOUNT AS A_TO ON A_TO.ID = T.TO_ACCOUNT
T.USER_ID = #{userId}
ORDER BY ID

mapper.update:
UPDATE TEMPLATE_TRANSFER
SET
FROM_ACCOUNT = #{fromAccount},
TO_ACCOUNT = #{toAccount},
DESCRIPTION = #{description}
WHERE
ID = #{id}
AND USER_ID = #{userId}

mapper.delete:
DELETE FROM TEMPLATE_TRANSFER

    ID = #{id}
    AND USER_ID = #{userId}
