mapper.update:
UPDATE TEMPORARY_TRANSFER
SET
FROM_ACCOUNT = #{fromAccount},
TO_ACCOUNT = #{toAccount},
DESCRIPTION = #{description},
AMOUNT = #{amount}
TRANSFER_ID = #{transferId},
WHERE
ID = #{id}
AND USER_ID = #{userId}

mapper.delete:
DELETE FROM TEMPORARY_TRANSFER

    ID = #{id}
    AND USER_ID = #{userId}
